css:
tr {
    page-break-inside: avoid;
    page-break-after: auto;
}
.print-format {
    font-size: 9.5pt;
    line-height: 1.5;
}
.print-format td, .print-format th {
    padding: 2px 4px !important;
}
table {
  border-collapse: collapse !important;
}
